mapper.update:
update salary
total_salary = #{total_salary}, update_time=NOW()
WHERE salary_id=#{salary_id}

update salary
base_salary = #{base_salary}, week_time = #{week_time}, festival_time = #{festival_time}, percentage = #{percentage}, bonus = #{bonus}, absence_count = #{absence_count}, late_count = #{late_count}, leave_count = #{leave_count}, update_time=NOW()
WHERE salary_id=#{salary_id}

UPDATE salary
base_salary = #{base_salary}, `leave` = #{leave}, late = #{late}, absence = #{absence}, overtime = #{overtime}, remark = #{remark}, updater = #{updater}, update_time=NOW()
WHERE salary_id=#{salary_id}

mapper.select:
select s.salary_id,s.user_id,s.base_salary,s.`leave`,s.late,s.absence,s.overtime,s.updater,s.update_time,u.login_name from salary s join user u on u.user_id=s.user_id
u.login_name like concat('%', #{login_name}, '%') and DATE_FORMAT(s.create_time,'%Y-%m') = #{create_time}
order by s.create_time desc
